import React from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import "./Mymain.css";
import { useEffect } from "react";
import axios from "axios";
import Dropdown from "./dropdown";
import FormInput from "./form";
import CountryCards from "./countryCards";
import Detailspage from "./details";
import { useState } from "react";

function Main() {
  const [selected, setSelected] = useState("Filter By Region");
  // const [countryDetail, setCountryDetail] = useState([]);
  const [countries, setCountries] = useState([]);
  const [input, setInput] = useState("");
  const searchParam = (input) => {
    setInput(input);
  };

  useEffect(() => {
    const searchCountry = async () => {
      if (selected === "Filter By Region" && input === "") {
        try {
          const response = await axios.get(
            `https://restcountries.com/v3.1/region/${"africa"}`
          );
          setCountries(response.data);
        } catch (error) {
          console.error("Error fetching data:", error);
        }
      } else if (selected !== "Filter By Region") {
        try {
          const response = await axios.get(
            `https://restcountries.com/v3.1/region/${selected}`
          );
          setCountries(response.data);
        } catch (error) {
          console.error("Error fetching data:", error);
        }
      }
      else {
        try {
          const response = await axios.get(
            `https://restcountries.com/v3.1/name/${input}`
          );
          console.log(response.data);
          setCountries(response.data);
        } catch (error) {
          console.error("Error fetching data:", error);
        }
      }
    };
    searchCountry();
  }, [input,selected]);

  // useEffect(() => {
  //   const searchCountry = async () => {
      // try {
      //   const response = await axios.get(
      //     `https://restcountries.com/v3.1/name/${input}`
      //   );
      //   console.log(response.data);
      //   setCountries(response.data);
      // } catch (error) {
      //   console.error("Error fetching data:", error);
      // }
  //   };
  //   searchCountry();
  // }, [input,countries]);

  // useEffect(() => {
  //   const searchCountry = async () => {
  //     if (selected !== "Filter By Region" ) {
  //       try {
  //         const response = await axios.get(
  //           `https://restcountries.com/v3.1/region/${selected}`
  //         );
  //         setCountries(response.data);
  //       } catch (error) {
  //         console.error("Error fetching data:", error);
  //       }
  //     } else {
  //       try {
  //         const response = await axios.get(
  //           `https://restcountries.com/v3.1/region/${"africa"}`
  //         );
  //         setCountries(response.data);
  //       } catch (error) {
  //         console.error("Error fetching data:", error);
  //       }
  //     }
  //   };
  //   searchCountry();
  // }, [selected]);

  return (
    <div className="main-div">
      <main>
        <div className="input-search-dropdown">
          <FormInput sendSearchParam={searchParam} />
          <Dropdown selected={selected} setSelected={setSelected} />
        </div>
        <Router>
          <div>
            <Routes>
              <Route
                path="/"
                exact
                element={<CountryCards countries={countries} />}
              />

              <Route
                path="/country/:countryCode"
                element={<Detailspage countries={countries} />}
              />
            </Routes>
          </div>
        </Router>
      </main>
    </div>
  );
}
export default Main;
