import React from "react";
import "./Mymain.css";
import { useEffect } from "react";
import axios from "axios";
import Dropdown from "./dropdown";
import FormInput from "./form";
import CountryCards from "./countryCards";
import { useState } from "react";
function Main() {
  const [selected, setSelected] = useState("Filter By Region");
  const [countries, setCountries] = useState([]);
  const [input, setInput] = useState("");
  const searchParam = (input) => {
    setInput(input);
  };

  useEffect(() => {
    const searchCountry = async () => {
      try {
        const response = await axios.get(
          `https://restcountries.com/v3.1/name/${input}`
        );

        // console.log(response.data);
        if (selected != "Filter By Region") {
          const arr = [];
          response.data.filter((item) => {
            // const arr = [...countries, ...item];
            // console.log(arr);
            // setCountries(arr);
            // console.log(item);

            if (item.continent[0] === selected) {
              arr.push(item);
              // setCountries([...countries, item]);
              // console.log([...countries, item]);
              // console.log(item);

              // console.log(selected);
            }
          });
          setCountries(arr);
        }
        else
        console.log(countries);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };
    searchCountry();
  }, [input]);
  // const [query, setQuery] = useState("");

  return (
    <div className="main-div">
      <main>
        <div className="input-search-dropdown">
          <FormInput sendSearchParam={searchParam} />
          <Dropdown selected={selected} setSelected={setSelected} />
        </div>
        <CountryCards countries={countries} />
      </main>
    </div>
  );
}
export default Main;
