import React from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import "./Mymain.css";
import { useEffect } from "react";
import axios from "axios";
import Dropdown from "./dropdown";
import FormInput from "./form";
import CountryCards from "./countryCards";
import Detailspage from "./details";
import { useState } from "react";

function Main() {
  // const [countryDetail, setCountryDetail] = useState([]);
  const [countries, setCountries] = useState([]);
  // default for search button onSunmit
  const searchParam = async (input) => {
    try {
      const res = await fetch(`https://restcountries.com/v3.1/name/${input}`);
      if (!res.ok) throw new Error("Not found in any country!");
      const data = await res.json();
      setCountries(data);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };
  // default for search button onSunmit

  // default browser
  useEffect(() => {
    const searchCountry = async () => {
      try {
        const response = await axios.get(`https://restcountries.com/v3.1/all`);
        setCountries(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };
    searchCountry();
  }, []);

  const getCountryByRegion = async (regionName) => {
    try {
      const res = await fetch(
        `https://restcountries.com/v3.1/region/${regionName}`
      );
      if (!res.ok) throw new Error("Not found in any country!");
      const data = await res.json();
      setCountries(data);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };
  // default browser

  

  return (
    <div className="main-div">
      <main>
        <div className="input-search-dropdown">
          <FormInput sendSearchParam={searchParam} />
          <Dropdown
            onSelect={getCountryByRegion}
            selected={selected}
            setSelected={setSelected}
          />
        </div>
        <Router>
          <div>
            <Routes>
              <Route
                path="/"
                exact
                element={<CountryCards countries={countries} />}
              />

              <Route
                path="/country/:countryCode"
                element={<Detailspage countries={countries} />}
              />
            </Routes>
          </div>
        </Router>
      </main>
    </div>
  );
}
export default Main;
