import React from "react";
import "./Mymain.css";
import { useEffect } from "react";
import { axios } from "axios";
import Dropdown from "./dropdown";
import FormInput from "./form";
// import CountryCards from "./countryCards";
import { useState } from "react";
function Main() {
  const [selected, setSelected] = useState("Filter By Region");
  const [countries, setCountries] = useState([]);
  const [input, setInput] = useState('');
  const searchParam = (input) => {
    
  setInput(input)
  }
  
  useEffect(() => {
    const searchCountry = async () => {
      try {
        const response = await axios.get(
          `https://restcountries.com/v3.1/name/${"africa"}`
        );

        setCountries(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };
    searchCountry();
  }, []);
  // const [query, setQuery] = useState("");

  return (
    <div className="main-div">
      <main>
        <div className="input-search-dropdown">
          <FormInput sendSearchParam={searchParam}></FormInput>
          <Dropdown selected={selected} setSelected={setSelected}></Dropdown>
        </div>
        {/* <CountryCards onQuery={setQuery}  ></CountryCards> */}
      </main>
    </div>
  );
}
export default Main;
