import sun from "./icons8-moon-50.png";
import moon from "./icons8-moon-50 (1).png";
import { useState } from "react";

function DarkTheme() {
    // const [lightMode, setLightMode] = useState(true);
  const handleChangeDarkTheme = (e) => {
    //   setLightMode((prevMode) => !prevMode);
    
  };
