  return (
    <div>
      <div className="header-span">
        <div onClick={handleChangeDarkTheme} >
          <img   alt="darkmode" width="20px" src={lightMode ? sun : moon} />
        </div>

        <p className="font-semibold">Dark Mode</p>
      </div>
    </div>
  );
}
export default DarkTheme;
